	CREATE PROCEDURE dbo.sp_alterdiagram
	(
		@diagramname 	sysname,
		@owner_id	int	= null,
		@version 	int,
		@definition 	varbinary(max)
	)
	WITH EXECUTE AS 'dbo'
	AS
	BEGIN
		set nocount on
	
		declare @theId 			int
		declare @retval 		int
		declare @IsDbo 			int
		
		declare @UIDFound 		int
		declare @DiagId			int
		declare @ShouldChangeUID	int
	
		if(@diagramname is null)
		begin
			RAISERROR ('Invalid ARG', 16, 1)
			return -1
		end
	
		execute as caller;
		select @theId = DATABASE_PRINCIPAL_ID();	 
		select @IsDbo = IS_MEMBER(N'db_owner'); 
		if(@owner_id is null)
			select @owner_id = @theId;
		revert;
	
		select @ShouldChangeUID = 0
		select @DiagId = diagram_id, @UIDFound = principal_id from dbo.sysdiagrams where principal_id = @owner_id and name = @diagramname 
		
		if(@DiagId IS NULL or (@IsDbo = 0 and @theId <> @UIDFound))
		begin
			RAISERROR ('Diagram does not exist or you do not have permission.', 16, 1);
			return -3
		end
	
		if(@IsDbo <> 0)
		begin
			if(@UIDFound is null or USER_NAME(@UIDFound) is null) -- invalid principal_id
			begin
				select @ShouldChangeUID = 1 ;
			end
		end

		-- update dds data			
		update dbo.sysdiagrams set definition = @definition where diagram_id = @DiagId ;

		-- change owner
		if(@ShouldChangeUID = 1)
			update dbo.sysdiagrams set principal_id = @theId where diagram_id = @DiagId ;

		-- update dds version
		if(@version is not null)
			update dbo.sysdiagrams set version = @version where diagram_id = @DiagId ;

		return 0
	END

CREATE PROCEDURE dbo.sp_creatediagram
(
	@diagramname 	sysname,
	@owner_id		int	= null, 	
	@version 		int,
	@definition 	varbinary(max)
)
WITH EXECUTE AS 'dbo'
AS
BEGIN
	set nocount on

	declare @theId int
	declare @retval int
	declare @IsDbo	int
	declare @userName sysname
	if(@version is null or @diagramname is null)
	begin
		RAISERROR (N'E_INVALIDARG', 16, 1);
		return -1
	end

	execute as caller;
	select @theId = DATABASE_PRINCIPAL_ID(); 
	select @IsDbo = IS_MEMBER(N'db_owner');
	revert; 
	
	if @owner_id is null
	begin
		select @owner_id = @theId;
	end
	else
	begin
		if @theId <> @owner_id
		begin
			if @IsDbo = 0
			begin
				RAISERROR (N'E_INVALIDARG', 16, 1);
				return -1
			end
			select @theId = @owner_id
		end
	end
	-- next 2 line only for test, will be removed after define name unique
	if EXISTS(select diagram_id from dbo.sysdiagrams where principal_id = @theId and name = @diagramname)
	begin
		RAISERROR ('The name is already used.', 16, 1);
		return -2
	end

	insert into dbo.sysdiagrams(name, principal_id , version, definition)
			VALUES(@diagramname, @theId, @version, @definition) ;
	
	select @retval = @@IDENTITY 
	return @retval
END

CREATE PROCEDURE [sp_CRM_AR_TAX](@REFNO as Varchar(100))
AS
BEGIN
	
	DELETE FROM CRM_AR_TAX_TEMP;
	
	DECLARE
	@idx as Int,
	@mRefNo as Varchar(100),

	@ID int,
	@Name VARCHAR(MAX),
	@Col_No int,
	@NoOfTaxes int, 
	@TDID int,
	@TDAmt int,

	@Colon INT,
	@Coma INT,

	@ST INT,

	@TaxDetails nvarchar(max)

	SET @mRefNo = @RefNo

	SET @ST = 1
	SET @idx = 1

	SET @NoOfTaxes = (SELECT Distinct NoOfTaxes FROM CRM_PaymentReceiptPosted WHERE RefNo = @mRefNo) 
	SET @TaxDetails = (SELECT Distinct TaxDetails FROM CRM_PaymentReceiptPosted WHERE RefNo = @mRefNo) 

	WHILE @idx<=@NoOfTaxes
		BEGIN

			IF (@NoOfTaxes > 1) 
				SET @TaxDetails = (SELECT TaxDetails FROM CRM_PaymentReceiptPosted WHERE AI_NO = @mRefNo) 

				SET @Coma = (SELECT dbo.INSTR(@TaxDetails, ',', 1, @idx) 
							 FROM CRM_PaymentReceiptPosted WHERE RefNo = @mRefNo) 
				SET @Colon = (SELECT dbo.INSTR(@TaxDetails, ':', 1, @idx) 
							  FROM CRM_PaymentReceiptPosted WHERE RefNo = @mRefNo) 

				SET @TDID = SUBSTRING(@TaxDetails,@ST,(@Colon-@ST))

				IF (@Coma = 0)
					SET @TDAmt = SUBSTRING(@TaxDetails,(@Colon+1),15)
				ELSE
					SET @TDAmt = SUBSTRING(@TaxDetails,(@Colon+1),(@Coma-(@Colon+1)))

				INSERT INTO CRM_AR_TAX_TEMP VALUES (@mRefNo, @TaxDetails, @TDID, @TDAmt, @Idx)

			SET @ST = 1 + @Coma 
			SET @idx = @idx + 1
		END

--SELECT * FROM CRM_AR_TAX_TEMP
END

