						WHERE RefNo = @mRefNo And AI_CO_ID = @mAI_CO_ID And AI_No = @AI_No)
						
	SET @TaxDetails = (SELECT TaxDetails 
						FROM CRM_PaymentReceiptPosted 
						WHERE RefNo = @mRefNo And AI_CO_ID = @mAI_CO_ID And AI_No = @AI_No)

	WHILE @idx<=@NoOfTaxes
		BEGIN

			IF (@NoOfTaxes >= 1) 
				SET @Coma = (SELECT dbo.INSTR(@TaxDetails, ',', 1, @idx) 
							 FROM CRM_PaymentReceiptPosted WHERE RefNo = @mRefNo And AI_CO_ID = @mAI_CO_ID And AI_No = @AI_No) 
				SET @Colon = (SELECT dbo.INSTR(@TaxDetails, ':', 1, @idx) 
							  FROM CRM_PaymentReceiptPosted WHERE RefNo = @mRefNo And AI_CO_ID = @mAI_CO_ID And AI_No = @AI_No) 

				SET @TDID = SUBSTRING(@TaxDetails,@ST,(@Colon-@ST))

				IF (@Coma = 0  OR @Coma IS NULL)
					SET @TDAmt = SUBSTRING(@TaxDetails,(@Colon+1),15)
				ELSE
					SET @TDAmt = SUBSTRING(@TaxDetails,(@Colon+1),(@Coma-(@Colon+1)))

				INSERT INTO CRM_AR_TAX_TEMP VALUES (@mRefNo, @TaxDetails, @AI_NO, @TDID, @TDAmt, @Idx)

			--SELECT @mRefNo, @TaxDetails, @AI_No, @TDID, @TDAmt, @Idx

			SET @ST = 1 + @Coma 
			SET @idx = @idx + 1
		END


END

